mapper.update:
UPDATE dept
SET
dname=#{dname}, dcode = #{dcode}, etc = #{etc}
WHERE deptno = #{deptno}

UPDATE STUPROFILE
SET
fname = #{fname}, PATH = #{path}
WHERE sno = #{sno}

mapper.select:
SELECT * FROM ( SELECT ROWNUM cnt, e.empno, e.ename, e.job, d.dname FROM EMPLOYEE e, DEPT d WHERE 1=1 AND e.DEPTNO = d.DEPTNO
AND e.ename LIKE '%'||#{ename}||'%'
AND e.deptno = #{deptno}
ORDER BY empno ) WHERE cnt BETWEEN #{start} AND #{end}

SELECT COUNT(*) FROM EMPLOYEE e WHERE 1=1
AND e.ename LIKE '%'||#{ename}||'%'
AND e.DEPTNO = #{deptno}

mapper.update:
UPDATE EMPLOYEE SET ename = #{ename}, job = #{job}, SALARY = #{salary}, ssnum = #{ssnum}, deptno = #{deptno}, phone = #{phone}, hiredate = to_date(#{hiredate}, 'YYYY-MM-DD'), ADDRESS = #{address}, ACCOUNT = #{account}, EMAIL = #{email}
, subject = #{subject}
WHERE empno = #{empno}

UPDATE EMPPROFILE
SET
fname = #{fname},
PATH = #{path}
WHERE empno = #{empno}

mapper.select:
SELECT a.empno,to_char(a.WORK_DATE, 'yy-MM-DD') AS WORK_DATE, a.ARR_TIME , a.DEP_TIME, late, EARLY_LEAVE, d.dname, TO_CHAR(TRUNC((DEP_TIME - ARR_TIME) * 24) - 1, 'FM00') || '시간 ' || TO_CHAR(ROUND(MOD((DEP_TIME - ARR_TIME) * 24 * 60, 60)), 'FM00') || '분' AS tot_workhours FROM ATTENDANCE a INNER JOIN EMPLOYEE e ON a.EMPNO = e.EMPNO INNER JOIN DEPT d ON e.DEPTNO = d.DEPTNO where 1 = 1
AND a.EMPNO = #{empno}
AND d.DEPTNO = #{deptno}
AND a.WORK_DATE BETWEEN #{start_date} AND #{end_date}
